/å¼€å§‹å€’è®¡æ—¶\n        countDownText.transform.parent.gameObject.SetActive(true);\n        StartCoroutine(\"Considerating\");\n    }\n    /// <summary>\n    /// æŠ¢åœ°ä¸»\n    /// </summary>\n    public void ForBid()\n    {\n        //å…³é—­å€’è®¡æ—¶\n        countDownText.transform.parent.gameObject.SetActive(false);\n        StopCoroutine(\"Considerating\");\n\n        CardManager._instance.ForBid();\n        isMyTerm = false;\n    }\n    /// <summary>\n    /// ä¸æŠ¢åœ°ä¸»\n    /// </summary>\n    public void NotBid()\n    {\n        //å…³é—­å€’è®¡æ—¶\n        countDownText.transform.parent.gameObject.SetActive(false);\n        StopCoroutine(\"Considerating\");\n\n        CardManager._instance.NotBid();\n        isMyTerm = false;\n    }\n    /// <summary>\n    /// å¡ç‰Œæ’åºï¼ˆä»å¤§åˆ°å°ï¼‰\n    /// </summary>\n    protected void Sort()\n    {\n        cardInfos.Sort();\n        cardInfos.Reverse();\n    }\n\n}\n```\n\n### è‡ªèº«ç©å®¶ç±»è°ƒæ•´\nå…¶å®å°±æ˜¯é‡å†™äº†åŸºç±»ToBidingæ–¹æ³•ï¼Œè°ƒç”¨æ˜¾ç¤ºå«ç‰ŒæŒ‰é’®ï¼ˆåªæœ‰è‡ªèº«ç©å®¶æ‰éœ€è¦ï¼‰\n```csharp\nusing System;\nusing System.Collections.Generic;\nusing DG.Tweening;\nusing UnityEngine;\nusing UnityEngine.EventSystems;\n\n/// <summary>\n/// è‡ªèº«ç©å®¶\n/// </summary>\npublic class PlayerSelf : Player\n{\n    public GameObject prefab;   //é¢„åˆ¶ä»¶\n\n    private Transform originPos1; //ç‰Œçš„åˆå§‹ä½ç½®\n    private Transform originPos2; //ç‰Œçš„åˆå§‹ä½ç½®\n    private List<GameObject> cards = new List<GameObject>();\n    private bool canSelectCard = false;     //ç©å®¶æ˜¯å¦å¯ä»¥é€‰ç‰Œ\n\n    void Awake()\n    {\n        originPos1 = transform.Find(\"OriginPos1\");\n        originPos2 = transform.Find(\"OriginPos2\");\n    }\n\n    //æ•´ç†æ‰‹ç‰Œ\n    public void GenerateAllCards()\n    {\n        //æ’åº\n        Sort();\n        //è®¡ç®—æ¯å¼ ç‰Œçš„åç§»\n        var offsetX = originPos2.position.x - originPos1.position.x;\n        //è·å–æœ€å·¦è¾¹çš„èµ·ç‚¹\n        int leftCount = (cardInfos.Count / 2);\n        var startPos = originPos1.position + Vector3.left * offsetX * leftCount;\n\n        for (int i = 0; i < cardInfos.Count; i++)\n        {\n            //ç”Ÿæˆå¡ç‰Œ\n            var card = Instantiate(prefab, originPos1.position, Quaternion.identity, transform);\n            card.GetComponent<RectTransform>().localScale = Vector3.one * 0.6f;\n            card.GetComponent<Card>().InitImage(cardInfos[i]);\n            card.transform.SetAsLastSibling();\n            //åŠ¨ç”»ç§»åŠ¨\n            var tween = card.transform.DOMoveX(startPos.x + offsetX * i, 1f);\n            if (i == cardInfos.Count - 1) //æœ€åä¸€å¼ åŠ¨ç”»\n            {\n                tween.OnComplete(() => { canSelectCard = true; });\n            }\n            cards.Add(card);\n        }\n    }\n    /// <summary>\n    /// é”€æ¯æ‰€æœ‰å¡ç‰Œå¯¹è±¡\n    /// </summary>\n    public void DestroyAllCards()\n    {\n        cards.ForEach(Destroy);\n        cards.Clear();\n    }\n    /// <summary>\n    /// å¼€å§‹å«ç‰Œ\n    /// </summary>\n    public override void ToBiding()\n    {\n        base.ToBiding();\n        CardManager._instance.SetBidButtonActive(true);\n    }\n    /// <summary>\n    /// ç‚¹å‡»å¡ç‰Œå¤„ç†\n    /// </summary>\n    /// <param name=\"data\"></param>\n    public void CardClick(BaseEventData data)\n    {\n        //å«ç‰Œæˆ–å‡ºç‰Œé˜¶æ®µæ‰å¯ä»¥é€‰ç‰Œ\n        if (canSelectCard &&\n            (CardManager._instance.cardManagerState == CardManagerStates.Bid ||\n             CardManager._instance.cardManagerState == CardManagerStates.Playing))\n        {\n            var eventData = data as PointerEventData;\n            if (eventData == null) return;\n\n            var card = eventData.pointerCurrentRaycast.gameObject.GetComponent<Card>();\n            if (card == null) return;\n\n            card.SetSelectState();\n        }\n    }\n}\n```\n\n### å¯¹æ‰‹ç©å®¶ç±»è°ƒæ•´\nä¸»è¦æ¨¡æ‹Ÿå¯¹æ‰‹å«ç‰Œï¼Œå¦‚æœæ˜¯ç©å®¶å›åˆï¼ŒæŒ‰ä¸‹Qå«ç‰Œï¼ŒæŒ‰ä¸‹Wä¸å«\n```csharp\nusing UnityEngine;\n\npublic class PlayerOther : Player\n{\n    void Update()\n    {\n        //å¦‚æœå½“å‰æ˜¯è‡ªå·±å›åˆï¼Œæ¨¡æ‹Ÿå¯¹æ‰‹å«ç‰Œ\n        if (isMyTerm)\n        {\n            if (Input.GetKeyDown(KeyCode.Q))    //å«ç‰Œ\n            {\n                ForBid();\n            }\n            if (Input.GetKeyDown(KeyCode.W))    //ä¸å«\n            {\n                NotBid();\n            }\n        }\n    }\n}\n```\n\n### å¡ç‰Œç®¡ç†ç±»è°ƒæ•´\né¦–å…ˆéœ€è¦æ”¹é€ å‘ç‰Œæ–¹æ³•ï¼Œç°åœ¨åŒºåˆ†å‘ç‰Œæ˜¯å‘æ™®é€šç‰Œè¿˜æ˜¯å‘å‘åœ°ä¸»ç‰Œï¼Œæ ¹æ®å‘ç‰Œçš„ä¸åŒç±»å‹ï¼Œå–ç‰Œå †é‡Œçš„ä¸åŒç‰Œï¼Œå†åˆ¤æ–­æ˜¯ä¾æ¬¡å‘ç‰Œï¼Œè¿˜æ˜¯åªå‘ç»™åœ°ä¸»ç‰Œã€‚\nç„¶åå…·ä½“å®ç°ç©å®¶å¼€å§‹æŠ¢åœ°ä¸»StartBidingï¼Œå«åœ°ä¸»ForBidï¼Œä¸å«åœ°ä¸»NotBid 3ä¸ªæ–¹æ³•ï¼š\nStartBidingï¼šä»å½“å‰å›åˆç©å®¶å¼€å§‹å«ç‰Œï¼›\nForBidï¼šè®¾ç½®å½“å‰å›åˆç©å®¶æ˜¯åœ°ä¸»ï¼Œå¹¶ç»™åœ°ä¸»å‘ä½™ä¸‹3å¼ ç‰Œï¼›\nNotBid ï¼šè½¬å‘ä¸‹ä¸ªå›åˆç©å®¶å¼€å§‹å«ç‰Œï¼›\n```csharp\nusing System;\nusing System.Collections;\nusing System.IO;\nusing System.Linq;\nusing DG.Tweening;\nusing UnityEngine;\n\n/// <summary>\n/// å¡ç‰Œç®¡ç†\n/// </summary>\npublic class CardManager : MonoBehaviour\n{\n    public static CardManager _instance;    //å•ä¾‹\n\n\n    public float dealCardSpeed = 20;  //å‘ç‰Œé€Ÿåº¦\n    public Player[] Players;    //ç©å®¶çš„é›†åˆ\n\n    public GameObject coverPrefab;      //èƒŒé¢æ’é¢„åˆ¶ä»¶\n    public Transform heapPos;           //ç‰Œå †ä½ç½®\n    public Transform[] playerHeapPos;    //ç©å®¶ç‰Œå †ä½ç½®\n    public CardManagerStates cardManagerState;\n\n    private string[] cardNames;  //æ‰€æœ‰ç‰Œé›†åˆ\n    private int termStartIndex;  //å›åˆå¼€å§‹ç©å®¶ç´¢å¼•\n    private int termCurrentIndex;  //å›åˆå½“å‰ç©å®¶ç´¢å¼•\n    private int bankerIndex;        //å½“å‰åœ°ä¸»ç´¢å¼•\n    private GameObject bidBtns;\n    void Awake()\n    {\n        _instance = this;\n        cardNames = GetCardNames();\n\n        bidBtns = GameObject.Find(\"BidBtns\");\n        bidBtns.SetActive(false);\n    }\n    /// <summary>\n    /// æ´—ç‰Œ\n    /// </summary>\n    public void ShuffleCards()\n    {\n 